// src/pages/BacktrackingVisualizer.jsx
import React, { useState } from "react";
import { motion } from "framer-motion";
import { backtrackingCodes } from "../data/codes"; // make sure this exists
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const algorithms = [
  { key: "nQueens", label: "N-Queens" },
  { key: "sudokuSolver", label: "Sudoku Solver" },
  { key: "ratInMaze", label: "Rat in a Maze" },
  { key: "subsetSum", label: "Subset Sum" },
  { key: "permutations", label: "Permutations" },
  { key: "wordSearch", label: "Word Search" },
  { key: "knightsTour", label: "Knight's Tour" },
  { key: "graphColoring", label: "Graph Coloring" },
  { key: "hamiltonianPath", label: "Hamiltonian Path" },
  { key: "palindromePartitioning", label: "Palindrome Partitioning" }
];

const langs = ["java", "python", "cpp", "javascript"];
const fadeInUp = {
  hidden: { opacity: 0, y: 40 },
  visible: { opacity: 1, y: 0, transition: { duration: 0.6 } },
};

const gifLinks = {
  nQueens: "https://blogs.mathworks.com/steve/files/queen-solver-animation-6.gif",
  sudokuSolver: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTPdhHmuxR62V_MWEwPS1R_GE8n-hvx_a_w5g&s",
  ratInMaze: "data:image/png;base64,",
  subsetSum: "data:image/jpeg;base64,",
  permutations: "https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEiDq5C0uyx__OffNjS31ovk8YX6hI-S4EuRrbtaTMgOkAhNTg3pQYEqLmawx_1yex_s8iYt6BLUbNEB67MlDx8f9xyZinknFNyVdX8E4uQ5imc_tH7rIjWvnD_E-6ubugwoKbTw-nOlHDYR/w1600/permutation.gif",
  wordSearch: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT0Wa-LWVRAcEFagLvCbxr8xVyU1cfb7YCynQ&s",
  knightsTour: "data:image/png;base64,",
  graphColoring: "data:image/png;base64,",
  hamiltonianPath: "https://upload.wikimedia.org/wikipedia/commons/c/cf/Panconnected_graph_animation.gif",
  palindromePartitioning: "data:image/png;base64,"
};

const BacktrackingVisualizer = () => {
  const [selectedAlgo, setSelectedAlgo] = useState("nQueens");
  const [selectedLang, setSelectedLang] = useState("java");

  return (
    <>
      <Navbar />
      <section className="px-8 md:px-20 pt-32 pb-16 bg-[#F9FAFB] text-[#1A1A1A]">
        {/* Header */}
        <motion.div className="text-center mb-12" initial="hidden" animate="visible" variants={fadeInUp}>
          <h1 className="text-5xl md:text-6xl font-bold text-[#1A1A1A]">
            Backtracking <span className="text-blue-600">Visualizer</span>
          </h1>
          <p className="mt-4 text-xl text-[#555555] max-w-3xl mx-auto">
            Visualize how backtracking algorithms explore all possibilities step by step. Select an algorithm and programming language to see its code and demo.
          </p>
        </motion.div>

        {/* Algorithm Buttons */}
        <motion.div className="flex flex-wrap justify-center gap-4 mb-8" initial="hidden" animate="visible" variants={fadeInUp}>
          {algorithms.map(algo => (
            <button
              key={algo.key}
              onClick={() => setSelectedAlgo(algo.key)}
              className={`px-6 py-2 rounded-full font-semibold transition shadow ${selectedAlgo === algo.key ? "bg-blue-600 text-white" : "bg-white text-[#1A1A1A] border border-gray-200 hover:bg-gray-100"}`}
            >
              {algo.label}
            </button>
          ))}
        </motion.div>

        {/* Language Buttons */}
        <motion.div className="flex justify-center gap-3 mb-6" initial="hidden" animate="visible" variants={fadeInUp}>
          {langs.map(lang => (
            <button
              key={lang}
              onClick={() => setSelectedLang(lang)}
              className={`px-5 py-2 rounded-full font-semibold transition shadow ${selectedLang === lang ? "bg-gray-800 text-white" : "bg-white text-[#1A1A1A] border border-gray-200 hover:bg-gray-100"}`}
            >
              {lang.toUpperCase()}
            </button>
          ))}
        </motion.div>

        {/* Code Display */}
        <motion.div className="mb-12" initial="hidden" animate="visible" variants={fadeInUp}>
          <div className="bg-white border border-gray-200 rounded-2xl shadow-md p-6 max-w-4xl mx-auto">
            <pre className="bg-[#1E1E1E] text-green-400 rounded-xl p-6 overflow-x-auto text-sm leading-relaxed">
              {backtrackingCodes[selectedAlgo]?.[selectedLang] || "// Code not available"}
            </pre>
          </div>
        </motion.div>

        {/* GIF Visualizer */}
        <motion.div className="mb-16" initial="hidden" animate="visible" variants={fadeInUp}>
          <div className="bg-white border border-gray-200 rounded-2xl shadow-md p-6 max-w-4xl mx-auto flex justify-center">
            <img
              src={gifLinks[selectedAlgo]}
              alt={selectedAlgo}
              className="rounded-xl shadow-lg max-w-full h-auto"
            />
          </div>
        </motion.div>
      </section>
      <Footer />
    </>
  );
};

export default BacktrackingVisualizer;
